begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(6279854455727215671)
,p_plugin_type=>'ITEM TYPE'
,p_name=>'COM_PROGENICSCORP_NUMBER_FORMAT'
,p_display_name=>'NumberFormat'
,p_supported_ui_types=>'DESKTOP'
,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('ITEM TYPE','COM_PROGENICSCORP_NUMBER_FORMAT'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'  /*************************************************************************************',
'  **************************************************************************************',
'  **  Plugin      : NumberFormat',
'  **  InternalName: COM_PROGENICSCORP_NUMBER_FORMAT',
'  **  Author      : Aderemi Alabi',
'  **  Date        : 03-MAY-2012',
'  **  Version     : 1.0',
'  **  Description : The plugin is used to format numeric values. During user input the ',
'  **                Item the formatting to accept values as it gains focus and ',
'  **                formats values once focus is lost',
'  **  Modification',
'  **  History',
'  **  {Who, What, When}  ',
'  ****************************************************************************************',
'  ***************************************************************************************/',
'  --',
'  -- Callback Render Function',
'  FUNCTION number_format_render(p_item                in apex_plugin.t_page_item,',
'                                p_plugin              in apex_plugin.t_plugin,',
'                                p_value               in varchar2,',
'                                p_is_readonly         in boolean,',
'                                p_is_printer_friendly in boolean) ',
'           RETURN apex_plugin.t_page_item_render_result IS',
'    -- ',
'    v_return apex_plugin.t_page_item_render_result;',
'    v_name   VARCHAR2(30);',
'    v_item   VARCHAR2(4000);',
'    v_value  VARCHAR2(100);',
'    v_style  VARCHAR2(100);',
'    v_d      VARCHAR2(10);  -- Decimal',
'    v_p      VARCHAR2(10);  -- Precision',
'    v_t      VARCHAR2(10);  -- Thousand Separator',
'    v_s      VARCHAR2(10);  -- Currency Symbol',
'    v_f      VARCHAR2(100);  -- Format',
'    --',
'  BEGIN',
'    --',
'    -- Printer Friendly Display',
'    IF p_is_printer_friendly THEN',
'      APEX_PLUGIN_UTIL.print_display_only (',
'		    p_item_name        => p_item.name,',
'		    p_display_value    => p_value,',
'		    p_show_line_breaks => false,',
'		    p_escape           => true,',
'		    p_attributes       => p_item.element_attributes );',
'    -- Read Only Display',
'    ELSIF p_is_readonly THEN',
'      APEX_PLUGIN_UTIL.print_hidden_if_readonly (',
'		    p_item_name           => p_item.name,',
'		    p_value               => p_value,',
'		    p_is_readonly         => p_is_readonly,',
'		    p_is_printer_friendly => p_is_printer_friendly );',
'    -- Normal Display',
'    ELSE',
'      --',
'      v_t     := ''''''''||NVL(p_item.attribute_01,'','')||'''''''';',
'      v_d     := ''''''''||NVL(p_item.attribute_02,''.'')||'''''''';',
'      v_p     := NVL(p_item.attribute_03,0);',
'      v_s     := ''''''''||p_item.attribute_04||'''''''';',
'      v_f     := ''''''''||p_item.attribute_05||'''''''';',
'      v_name  := APEX_PLUGIN.get_input_name_for_page_item(p_is_multi_value=>false);',
'      v_value := htf.escape_sc(p_value);',
'      v_style := ''style="text-align:right;" '';',
'      v_item  :=  ''<input '';',
'      v_item  := v_item || ''type="text" '';',
'      v_item  := v_item || ''name="''||v_name||''" '';',
'      v_item  := v_item || ''id="''||p_item.name||''" '';',
'      v_item  := v_item ||''class="text_field apex-item-text"'';',
'      v_item  := v_item || ''value="''||v_value||''" '';',
'      v_item  := v_item || ''size="''||p_item.element_width||''" '';',
'      v_item  := v_item || ''maxlength="''||p_item.element_max_length||''" '';',
'      v_item  := v_item || ''maxlength="''||p_item.element_max_length||''" '';',
'      v_item  := v_item || v_style;',
'      v_item  := v_item || '' onfocus="gotFocus(this.id)"'';',
'      v_item  := v_item || '' onblur="lostFocus(this.id,''||v_p||'',''||v_d||'',''||v_t||'',''||v_s||'',''||v_f||'')"'';',
'      v_item  := v_item || '' />'';',
'      htp.p(v_item);',
'      --',
'      APEX_JAVASCRIPT.add_library (',
'        p_name      => ''com_progenicscorp_format'',',
'        p_directory => p_plugin.file_prefix,',
'        p_version   => null );',
'      APEX_JAVASCRIPT.add_library (',
'        p_name      => ''accounting'',',
'        p_directory => p_plugin.file_prefix,',
'        p_version   => null );',
'      --',
'    END IF;',
'    --',
'    RETURN v_return;',
'    --',
'  END number_format_render;'))
,p_api_version=>1
,p_render_function=>'number_format_render'
,p_standard_attributes=>'VISIBLE:SESSION_STATE:READONLY:WIDTH'
