 p_id=>wwv_flow_api.id(1878574279092972811)
,p_plugin_type=>'ITEM TYPE'
,p_name=>'LAURESTON.STARRATING'
,p_display_name=>'Laureston Star Rating'
,p_supported_ui_types=>'DESKTOP:JQM_SMARTPHONE'
,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('ITEM TYPE','LAURESTON.STARRATING'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'procedure render_rateyo(',
'    p_item   in            apex_plugin.t_item,',
'    p_plugin in            apex_plugin.t_plugin,',
'    p_param  in            apex_plugin.t_item_render_param,',
'    p_result in out nocopy apex_plugin.t_item_render_result) as',
'',
'  l_jquery_onload varchar2(4000) := q''!',
'$("##ITEM_NAME#_RATEYO").rateYo({',
'  #STAR_WIDTH#',
'  #NORMAL_FILL#',
'  #RATED_FILL#',
'  #NUM_STARS#',
'  #SELECTION_TYPE#',
'  #SPACING#',
'  #RTL#',
'  #READONLY#',
'  #RATING#',
'  #STARSVG#',
'  onSet: function(rating, rateYoInstance){',
'    $("##ITEM_NAME#").val(rating);',
'  }',
'});',
'!'';',
'  ',
'  l_starWidth p_item.attribute_01%TYPE := p_item.attribute_01;',
'  l_normalFill p_item.attribute_02%TYPE := p_item.attribute_02;',
'  l_ratedFill p_item.attribute_03%TYPE := p_item.attribute_03;',
'  l_numStars p_item.attribute_04%TYPE := p_item.attribute_04;',
'  l_precision p_item.attribute_05%TYPE := p_item.attribute_05;',
'  l_selectionType p_item.attribute_06%TYPE := p_item.attribute_06;',
'  l_spacing p_item.attribute_07%TYPE := p_item.attribute_07;',
'  l_rtl p_item.attribute_08%TYPE := p_item.attribute_08;',
'  l_fill_type p_item.attribute_11%TYPE := p_item.attribute_11;',
'  l_start_color p_item.attribute_09%TYPE := p_item.attribute_09;',
'  l_end_color p_item.attribute_10%TYPE := p_item.attribute_10;',
'  l_star_svg p_item.attribute_12%TYPE := p_item.attribute_12;',
'begin',
'  -- Load CSS Libraries',
'  apex_css.add_file(p_name => ''jquery.rateyo.min'', p_directory => p_plugin.file_prefix, p_version => null);',
'  ',
'  -- Load JS Libraries',
'  apex_javascript.add_library(p_name => ''jquery.rateyo.min'', p_directory => p_plugin.file_prefix, p_version => null);',
'  ',
'  ',
'  IF apex_application.g_debug THEN',
'    apex_plugin_util.debug_page_item(p_plugin, p_item);',
'  END IF;',
'  ',
'  --sys.htp.p(APEX_ITEM.HIDDEN(1, p_param.value, null, p_item.name, null));',
'  sys.htp.p(''<input type="hidden" id="''||p_item.name||''" name="''||p_item.name||''" value="''||p_param.value||''" />'');',
'  sys.htp.p(''<div id="''||p_item.name||''_RATEYO"></div>'');',
'  ',
'  l_jquery_onload := replace(l_jquery_onload, ''#ITEM_NAME#'', p_item.name);',
'  l_jquery_onload := replace(l_jquery_onload, ''#ITEM_VALUE#'', p_param.value);',
'  ',
'  if p_param.value is not null then',
'    l_jquery_onload := replace(l_jquery_onload, ''#RATING#'', ''rating: ''||p_param.value||'','');',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#RATING#'', null);',
'  end if;',
'  ',
'  if l_starWidth is not null then',
'    l_jquery_onload := replace(l_jquery_onload, ''#STAR_WIDTH#'', ''starWidth: "''||l_starWidth||''px",'');',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#STAR_WIDTH#'', null);',
'  end if;',
'  ',
'  if l_normalFill is not null then',
'    l_jquery_onload := replace(l_jquery_onload, ''#NORMAL_FILL#'', ''normalFill: "''||l_normalFill||''",'');',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#NORMAL_FILL#'', null);',
'  end if;',
'  ',
'  if l_fill_type = ''NORMAL'' then',
'    if l_ratedFill is not null then',
'      l_jquery_onload := replace(l_jquery_onload, ''#RATED_FILL#'', ''ratedFill: "''||l_ratedFill||''",'');',
'    else',
'      l_jquery_onload := replace(l_jquery_onload, ''#RATED_FILL#'', null);',
'    end if;',
'  elsif l_fill_type = ''RANGE'' then',
'    if l_start_color is not null and l_end_color is not null then',
'      l_jquery_onload := replace(l_jquery_onload, ''#RATED_FILL#'', ''multiColor: {"startColor": "''||l_start_color||''","endColor": "''||l_end_color||''"},'');',
'    else',
'      l_jquery_onload := replace(l_jquery_onload, ''#RATED_FILL#'', null);',
'    end if;',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#RATED_FILL#'', null);',
'  end if;',
'  ',
'  if l_numStars is not null then',
'    l_jquery_onload := replace(l_jquery_onload, ''#NUM_STARS#'', ''numStars: ''||l_numStars||'','');',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#NUM_STARS#'', null);',
'  end if;',
'  ',
'  if l_spacing is not null then',
'    l_jquery_onload := replace(l_jquery_onload, ''#SPACING#'', ''spacing: "''||l_spacing||''px",'');',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#SPACING#'', null);',
'  end if;',
'  ',
'  if l_selectionType is not null then',
'    if l_selectionType = ''FULL_STAR'' then',
'      l_jquery_onload := replace(l_jquery_onload, ''#SELECTION_TYPE#'', ''fullStar: true,'');',
'    elsif l_selectionType = ''HALF_STAR'' then',
'      l_jquery_onload := replace(l_jquery_onload, ''#SELECTION_TYPE#'', ''halfStar: true,'');',
'    elsif l_selectionType = ''PRECISION'' then',
'      if l_precision is not null then',
'        l_jquery_onload := replace(l_jquery_onload, ''#SELECTION_TYPE#'', ''precision: ''||l_precision||'','');',
'      else',
'        l_jquery_onload := replace(l_jquery_onload, ''#SELECTION_TYPE#'', null);',
'      end if;',
'    else',
'      l_jquery_onload := replace(l_jquery_onload, ''#SELECTION_TYPE#'', null);',
'    end if;',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#SELECTION_TYPE#'', null);',
'  end if;',
'  ',
'  if l_rtl = ''Y'' then',
'    l_jquery_onload := replace(l_jquery_onload, ''#RTL#'', ''rtl: true,'');',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#RTL#'', null);',
'  end if;',
'  ',
'  if p_param.is_readonly then',
'    l_jquery_onload := replace(l_jquery_onload, ''#READONLY#'', ''readOnly: true,'');',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#READONLY#'', null);',
'  end if;',
'  ',
'  if l_star_svg is not null then',
'    l_jquery_onload := replace(l_jquery_onload, ''#STARSVG#'', ''starSvg: ''''''||l_star_svg||'''''','');',
'  else',
'    l_jquery_onload := replace(l_jquery_onload, ''#STARSVG#'', null);',
'  end if;',
'  ',
'  apex_javascript.add_onload_code(l_jquery_onload);',
'end;'))
,p_api_version=>2
,p_render_function=>'render_rateyo'
,p_standard_attributes=>'VISIBLE:FORM_ELEMENT:SESSION_STATE:READONLY:ESCAPE_OUTPUT:SOURCE:ELEMENT:ELEMENT_OPTION:FILTER'
