begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(23113035579819174682)
,p_plugin_type=>'ITEM TYPE'
,p_name=>'COM.FARZAD.APEXGAUGE'
,p_display_name=>'APEX D3 Liquid Gauge'
,p_supported_ui_types=>'DESKTOP'
,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('ITEM TYPE','COM.FARZAD.APEXGAUGE'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION render_item(p_item                IN apex_plugin.t_page_item,',
'                     p_plugin              IN apex_plugin.t_plugin,',
'                     p_value               IN VARCHAR2,',
'                     p_is_readonly         IN BOOLEAN,',
'                     p_is_printer_friendly IN BOOLEAN)',
'  RETURN apex_plugin.t_page_item_render_result IS',
'  -- Custom Plugin Attributes',
'  l_result             apex_plugin.t_page_item_render_result;',
'  l_input_percent      p_item.attribute_02%TYPE := apex_escape.html(p_item.attribute_02);',
'  l_sql_percent        p_item.attribute_03%TYPE := apex_escape.html(p_item.attribute_03);',
'  l_height             p_item.attribute_04%TYPE := apex_escape.html(p_item.attribute_04);',
'  l_theme              p_item.attribute_05%TYPE := apex_escape.html(p_item.attribute_05);',
'  l_circleColour       p_item.attribute_06%TYPE := apex_escape.html(p_item.attribute_06);',
'  l_textColour         p_item.attribute_07%TYPE := apex_escape.html(p_item.attribute_07);',
'  l_waveTextColour     p_item.attribute_08%TYPE := apex_escape.html(p_item.attribute_08);',
'  l_waveColour         p_item.attribute_09%TYPE := apex_escape.html(p_item.attribute_09);',
'  -- Other Variables',
'  l_name            VARCHAR2(30);',
'  l_percent         VARCHAR2(30);',
'  l_config          VARCHAR2(5000);',
'  l_escaped_value   VARCHAR2(4000);',
'  l_html_string     VARCHAR2(2000);',
'  l_element_item_id VARCHAR2(200);',
'  l_onload_code     VARCHAR2(14000);',
'  l_temp            VARCHAR2(4000);',
'BEGIN',
'  -- Printer Friendly Display',
'  IF p_is_printer_friendly THEN',
'    apex_plugin_util.print_display_only(p_item_name        => p_item.name,',
'                                        p_display_value    => p_value,',
'                                        p_show_line_breaks => FALSE,',
'                                        p_escape           => TRUE,',
'                                        p_attributes       => p_item.element_attributes);',
'  -- Read Only Display',
'  ELSIF p_is_readonly THEN',
'    apex_plugin_util.print_hidden_if_readonly(p_item_name           => p_item.name,',
'                                              p_value               => p_value,',
'                                              p_is_readonly         => p_is_readonly,',
'                                              p_is_printer_friendly => p_is_printer_friendly);',
'  -- Normal Display',
'  ELSE',
'    l_element_item_id := p_item.name;',
'    l_name            := apex_plugin.get_input_name_for_page_item(FALSE);',
'    l_escaped_value   := apex_escape.html(p_value);',
'    ',
'    -- Build SVG HTML String',
'    l_html_string := ''<svg '';',
'    l_html_string := l_html_string || ''id="'' || l_element_item_id || ''" '';',
'    l_html_string := l_html_string || ''width="100%" height="'' || l_height || ''"></svg>'';',
'',
'    -- Add JavaScript Files',
'    apex_javascript.add_library(p_name           => ''d3.v3.min'',',
'                                p_directory => p_plugin.file_prefix,',
'                                p_version        => NULL,',
'                                p_skip_extension => FALSE);',
'    apex_javascript.add_library(p_name           => ''liquidFillGauge'',',
'                                p_directory => p_plugin.file_prefix,',
'                                p_version        => NULL,',
'                                p_skip_extension => FALSE);',
'    -- Instantiate onLoad Code',
'        -- Input or SQL Query for Percent',
'    if l_input_percent is not null then',
'        l_percent := l_input_percent;',
'    elsif l_sql_percent is not null then',
'        l_sql_percent := ''SELECT ('' || l_sql_percent || '') FROM DUAL'';',
'        execute immediate l_sql_percent into l_percent;',
'    end if;',
'        -- Configurations',
'    l_config := ''var config1 = liquidFillGaugeDefaultSettings();'' ||',
'                ''%CONFIGURATION%'';',
'        -- Themes',
'    if l_theme = 1 then -- Theme 1',
'        l_temp := ''config1.circleColor = "''   || NVL(l_circleColour, ''#FF7777'')   || ''";''   ||',
'                  ''config1.textColor = "''     || NVL(l_textColour, ''#FF4444'')     || ''";''   ||',
'                  ''config1.waveTextColor = "'' || NVL(l_waveTextColour, ''#FFAAAA'') || ''";''   ||',
'                  ''config1.waveColor = "''     || NVL(l_waveColour, ''#FFDDDD'')     || ''";''   ||',
'                  ''config1.circleThickness = 0.2;''     ||',
'                  ''config1.textVertPosition = 0.2;''    ||',
'                  ''config1.waveAnimateTime = 1000;'';',
'        l_config := REPLACE(l_config, ''%CONFIGURATION%'', l_temp);',
'    elsif l_theme = 2 then -- Theme 2',
'        l_temp := ''config1.circleColor = "''   || NVL(l_circleColour, ''#D4AB6A'')   || ''";''   ||',
'                  ''config1.textColor = "''     || NVL(l_textColour, ''#553300'')     || ''";''   ||',
'                  ''config1.waveTextColor = "'' || NVL(l_waveTextColour, ''#805615'') || ''";''   ||',
'                  ''config1.waveColor = "''     || NVL(l_waveColour, ''#AA7D39'')     || ''";''   ||',
'                  ''config1.circleThickness = 0.1;''  ||',
'                  ''config1.circleFillGap = 0.2;''    ||',
